import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Card, CardTitle, Col, Row } from 'reactstrap';

export default function Pregunta(props) {

    const [colores, setColores] = useState(() => {
        const colorinicial = {};
        props.preguns.map((pregunta, index) => {
            colorinicial[index] = pregunta.respuestas.map(() => props.color[0]);
        });
        return colorinicial;
    });
    const [botonclick, setBotonclick] = useState({});

    const handleClick = (i, bi, valor) => {
        // Verificar si el botón seleccionado ya está marcado
        if (botonclick[i] === bi) {
            return; // No hacer nada si el botón ya está marcado
        }

        // Crear una copia del objeto de colores
        const nuevocolor = { ...colores };
        // Cambiar el color del botón presionado en la tarjeta específica
        // Desmarcar todos los botones en la tarjeta
        nuevocolor[i] = nuevocolor[i].map((color, index) => {
            return index === bi ? props.color[1] : props.color[0];
        });
        // Actualizar el estado de los colores
        setColores(nuevocolor);
        // Guardar el índice del último botón seleccionado en esta tarjeta
        setBotonclick({ ...botonclick, [i]: bi });
        // Sumar el valor del botón al contador
        props.contar(valor);
    };

    return (
        <Row>
            <Col sm="6">
                {props.preguns.map((pregunta, i) => (
                    <Card
                        key={i}
                        body
                        className="my-2"
                        style={{ width: '30rem' }}
                    >
                        <CardTitle tag="h5">{pregunta.texto}</CardTitle>
                        {pregunta.respuestas.map((respuesta, bi) => (
                            <Button
                                key={bi}
                                color={colores[i][bi]} // Usar el color correspondiente desde el estado
                                outline
                                onClick={() => handleClick(pregunta.orden, respuesta.valor)}
                            >
                                {respuesta.respuesta}
                            </Button>
                        ))}
                    </Card>
                ))}
            </Col>
        </Row>
    );
}
