import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Card, CardTitle, Col, Row } from 'reactstrap';

export default function Pregunta(props) {
    const [colores, setColores] = useState(Array(props.preguns.length).fill(props.color1)); // Inicializa los colores de los botones con el color1

    const handleClick = (index, valor) => {
        const newColores = [...colores];
        newColores[index] = 'success'; // Cambia el color del botón presionado
        setColores(newColores); // Actualiza el estado de los colores
        props.contar(valor);
    };

    return (
        <Row>
            <Col sm="6">
                {props.preguns.map((pregunta, index) => (
                    <Card
                        key={index}
                        body
                        className="my-2"
                        style={{ width: '30rem' }}
                    >
                        <CardTitle tag="h5">{pregunta.texto}</CardTitle>
                        {pregunta.respuestas.map((respuesta, idx) => (
                            <Button
                                key={idx}
                                color={colores[index]} // Usa el color correspondiente desde el estado
                                outline
                                onClick={() => handleClick(index, respuesta.valor)}
                            >
                                {respuesta.respuesta}
                            </Button>
                        ))}
                    </Card>
                ))}
            </Col>
        </Row>
    );
}
