import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Card, CardTitle, Col, Row } from 'reactstrap';

export default function Preguntas({ preguntas, contar, color }) {
    const [coloresBotones, setColoresBotones] = useState(() => {
        const coloresIniciales = preguntas.map((pregunta, index) => {
            return props.result.map(() => color[0]);
        });
        return coloresIniciales;
    });

    const [ultimoBotonClickeado, setUltimoBotonClickeado] = useState({});

    const manejarClick = (indiceTarjeta, indiceBoton, valor) => {
        if (ultimoBotonClickeado[indiceTarjeta] === indiceBoton) {
            return;
        }

        const nuevosColores = [...coloresBotones];
        nuevosColores[indiceTarjeta] = nuevosColores[indiceTarjeta].map((color, index) => {
            return index === indiceBoton ? color[1] : color[0];
        });
        setColoresBotones(nuevosColores);
        setUltimoBotonClickeado({ ...ultimoBotonClickeado, [indiceTarjeta]: indiceBoton });
        contar(valor);
    };

    return (
        <Row>
            <Col sm="6">
                {PaymentResponse.result.map((pregunta, indiceTarjeta) => (
                    <Card
                        key={indiceTarjeta}
                        body
                        className="my-2"
                        style={{ width: '30rem' }}
                    >
                        <CardTitle tag="h5">{pregunta.texto}</CardTitle>
                        {pregunta.respuestas.map((respuesta, indiceBoton) => (
                            <Button
                                key={indiceBoton}
                                color={coloresBotones[indiceTarjeta][indiceBoton]}
                                outline
                                onClick={() => manejarClick(indiceTarjeta, indiceBoton, respuesta.valor)}
                            >
                                {respuesta.respuesta}
                            </Button>
                        ))}
                    </Card>
                ))}
            </Col>
        </Row>
    );
}
