import React, { useCallback, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Button, Card, CardTitle, Col, Row,
} from 'reactstrap';


export default function Pregunta(props) {
    const handleClick = (valor) => {
        return () => {
            props.contar(valor);
        };
    };
    const [col,setCol]=useState(['info','succes'])

    return (
        <Row>
            <Col sm="6">
                {props.preguns.map((pregunta, index) => (
                    <Card
                        key={index}
                        body
                        className="my-2"
                        style={{
                            width: '30rem'
                        }}
                    >
                        <CardTitle tag="h5">
                            {pregunta.texto}
                        </CardTitle>
                        {pregunta.respuestas.map((respuesta, index) => (
                            <Button key={index} color={props.color1} outline onClick={handleClick(respuesta.valor)}>
                                {respuesta.respuesta}
                            </Button>
                        ))}
                    </Card>
                ))}
            </Col>
        </Row>
    );
}
